    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String s1, s2, s3;
        s1 = jTextField1.getText();
        s2 = jTextField2.getText();
        s3 = jTextField3.getText();
        
        int s4 = Integer.parseInt(s1);
        
        Shape emptyShape = new Shape();
        Shape newShape = new Shape (s4,s2,s3);
        
        String output = "New shape create: \n---------------------\nShape ID: " + newShape.getShapeID() + "\nShape name: " +
                       newShape.getShapeName() + "\nShape Color: " + newShape.getShapeColor();
        
        jTextArea1.setText(output);
    }//GEN-LAST:event_jButton1ActionPerformed

