    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
         String s1, s2, s3;
        s1 = jTextField1.getText();
        s2 = jTextField2.getText();
        s3 = jTextField3.getText();
        int s4 = Integer.parseInt(s1);
         
        String s5 = jTextField4.getText();
        
        double s7 = Double.parseDouble(s5);
        
        Circle emptyCircle = new Circle();
        Circle newCircle = new Circle(s7,s4,s2,s3);
        
        String output = "New shape create: \n---------------------\nShape ID: " + newCircle.getShapeID() + "\nShape name: " +
                       newCircle.getShapeName() + "\nShape Color: " + newCircle.getShapeColor() + "\nRadius of Circle: " +
                       newCircle.getRadius() + "\nCircle diameter: " + newCircle.calculateDiameter() + "\nCircumferance: " +
                       newCircle.calculateCircumferance() + "\nArea: " + newCircle.calculateArea();
        
        jTextArea1.setText(output);
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
        String s1, s2, s3;
        s1 = jTextField1.getText();
        s2 = jTextField2.getText();
        s3 = jTextField3.getText();
        int s4 = Integer.parseInt(s1);
        
        String s5 = jTextField4.getText();
        String s6 = jTextField5.getText();
        
        double s7 = Double.parseDouble(s5);
        double s8 = Double.parseDouble(s6);
        
        Cylinder emptyCylider = new Cylinder();
        Cylinder newCylinder = new Cylinder(s7,s8);
        
        Circle newCircle = new Circle(s7,s4,s2,s3);
       
        
         String output = "New shape create: \n---------------------\nShape ID: " + newCircle.getShapeID() + "\nShape name: " +
                       newCircle.getShapeName() + "\nShape Color: " + newCircle.getShapeColor() + "\nRadius of Circle: " +
                       newCircle.getRadius() + "\nCircle diameter: " + newCircle.calculateDiameter() + "\nCircumferance: " +
                       newCircle.calculateCircumferance() + "\nArea: " + newCircle.calculateArea()+ "\nHeight of Cylinder: " + newCylinder.getHeight() + "\nSurface Area: " + 
                       newCylinder.calculateSurfaceArea();
        jTextArea1.setText(output);
    }//GEN-LAST:event_jButton3ActionPerformed

